// Place global declarations here.
const int AgentNum = 5;
typedef int[0,AgentNum-1] AgentScale;
const int TaskNum = 4;
const int EventNum = 0;
const int MilestoneNum = 4;
const int MaxIteration = 2;
const int RegularTaskNum[AgentNum] = {3,3,3,3,3};
const int MAXTIME = 2000;
//const int REWARDRATE = 2000;

int[-1,MilestoneNum] position[AgentNum] = {0,0,0,0,0};
bool tf[AgentNum][TaskNum]={{true,false,false,false},{true,false,false,false},{true,false,false,false},{true,false,false,false},{true,false,false,false}};
bool ts[AgentNum][TaskNum]={{true,false,false,false},{true,false,false,false},{true,false,false,false},{true,false,false,false},{true,false,false,false}};
const int[0,TaskNum] rules[MilestoneNum] = {0,1,2,3};

int iteration[AgentNum] = {0,0,0,0,0};

clock globalTime;
bool idle[AgentNum] = {true,true,true,true,true};

broadcast chan someoneDone[MilestoneNum];

int tasks_finished() 
{
    int a;
    int t;
    int finished = 0;
    for(a : AgentScale) 
    {
        for(t = 0; t < TaskNum; ++t)
        {
            if(tf[a][t]) ++finished;
        }
        finished += iteration[a]*TaskNum;
    }
    return finished;
}

