bool isNecessary(int agentID, int taskID)
{
    return !tf[agentID][taskID]&&iteration[agentID]<MaxIteration;
}

bool isBusy(int taskID)
{
    int id = 0;
    bool busy = false;


    for(id = 0; id < AgentNum; id++)
    {
        if(ts[id][taskID])
        {
            busy = true;
            return busy;
        }
    }

    return busy;
}

bool isReady(int agentID, int currentPosition, int targetPosition)
{
    /*int currentTask = rules[currentPosition];
    int targetTask = rules[targetPosition];
    bool currentFinished = tf[agentID][currentTask];
    bool targetFinished = tf[agentID][targetTask];
    bool rightChoice = currentFinished&!targetFinished;*/
    bool ready = idle[agentID]&&iteration[agentID]<MaxIteration;

    //return ready&&(rightChoice||mistakes[agentID]<4);
    return ready;
}

bool shouldWait(int agentID)
{
    int currentPosition, currentTask;
    bool currentFinished, ready, wait, busy;

    currentPosition = position[agentID];
    if(currentPosition != -1)
    {
        currentTask = rules[currentPosition];
        currentFinished = tf[agentID][currentTask];
        ready = idle[agentID]&&iteration[agentID]<MaxIteration;
        busy = isBusy(currentTask);
        wait = !currentFinished && ready && busy;
    }
    else
    {
        wait = false;
    }

    return wait;   
}

template.declaration:
clock t;

void updateIteration()
{
	int i=0;
	bool finish = true;
	for(i=1;i<=RegularTaskNum[id];i++)
	{
		finish &= tf[id][i];
	}
	if(finish)
	{
		for(i=1;i<=RegularTaskNum[id];i++)
		{
			tf[id][i]=false;
		}

		iteration[id]+=1;
		if(iteration[id]>=MaxIteration)
		{
			iteration[id]=MaxIteration;
		}
	}
	else
	{
    	finish = true;
	}
}

